import { from } from "pumpify";
import React, { useState } from "react";
import "./ExpenseForm.css";

function ExpenseForm(event) {

    // **** Multiple state concept ****

  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

    // **** one state concept ****

  function titleEventHandler() {
      setEnteredTitle(event.target.value);
  }
  function amountEventHandler() {
    setEnteredAmount(event.target.value);
}
function dateEventHandler() {
    setEnteredDate(event.target.value);
}

  return (
    <from>
      <div className="new-expense__control">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleEventHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountEventHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Title</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateEventHandler}/>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
        </div>
      </div>
    </from>
  );
}

export default ExpenseForm;
