import React, { useState } from "react";

import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [filteredExpense,setFilteredExpense] = useState(props.items);
  function saveExpensesYearHandler(selectedYear) {
    setFilteredYear(selectedYear);
    const filteredArray = filteredExpense.filter(filter => filter.getFullYear() == selectedYear)
    setFilteredExpense()
    
  }
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          seleced={filteredYear}
          onChangeExpenseYear={saveExpensesYearHandler}
        />
        {filteredExpense.map((expenses) => (
          <ExpenseItem
            key ={expenses.id}
            title={expenses.title}
            amount={expenses.amount}
            date={expenses.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
